import os
import json
from pathlib import Path
from typing import Any, Dict, Optional, Tuple, List

from config_loader import layer_bonuses as _layer_bonuses_cfg, path_boosts as _path_boosts_cfg


_OVERRIDES: Dict[str, Any] | None = None

_overrides_path() -> Path:
    # Keep with UI assets for simplicity
    return Path(__file__).parent / "ui" / "runtime_overrides.json"

_load_overrides() -> Dict[str, Any]:
    global _OVERRIDES
    if _OVERRIDES is not None:
        return _OVERRIDES
    p = _overrides_path()
    if p.exists():
        try:
            _OVERRIDES = json.loads(p.read_text())
        except Exception:
            _OVERRIDES = {}
    else:
        _OVERRIDES = {}
    return _OVERRIDES

_get_override(repo: Optional[str], key: str) -> Any:
    ov = _load_overrides()
    # Precedence: per-repo -> _global
    if repo:
        rp = ov.get(repo)
        if isinstance(rp, dict) and key in rp:
            return rp[key]
    g = ov.get("_global")
    if isinstance(g, dict) and key in g:
        return g[key]
    return None

_coerce(value: Any, typ: str) -> Any:
    if value is None:
        return None
    try:
        if typ == "int":
            return int(value)
        if typ == "float":
            return float(value)
        if typ == "bool":
            if isinstance(value, bool):
                return value
            s = str(value).strip().lower()
            return s in {"1", "true", "yes", "on"}
        if typ == "str":
            return str(value)
        if typ == "list[str]":
            if isinstance(value, list):
                return [str(x) for x in value]
            return [x.strip() for x in str(value).split(',') if x.strip()]
    except Exception:
        return None
    return value

get_str(repo: Optional[str], key: str, env_key: Optional[str] = None, default: Optional[str] = None) -> Optional[str]:
    v = _get_override(repo, key)
    if v is None and env_key:
        v = os.getenv(env_key)
    return _coerce(v if v is not None else default, "str")

get_int(repo: Optional[str], key: str, env_default: Optional[int] = None, default: Optional[int] = None) -> int:
    v = _get_override(repo, key)
    if v is None:
        v = env_default
    v = default if v is None else v
    out = _coerce(v, "int")
    return int(out) if out is not None else int(default or 0)

get_path_boosts(repo: Optional[str]) -> List[str]:
    # Use repos.json, with optional env override per-repo (e.g., PROJECT_PATH_BOOSTS)
    lst = _path_boosts_cfg(repo or "")
    env_key = f"{(repo or '').upper()}_PATH_BOOSTS" if repo else None
    if env_key:
        env_val = os.getenv(env_key)
        if not env_val and (repo or "").lower() == "project":
            env_val = os.getenv("project_PATH_BOOSTS")
        if env_val:
            lst.extend([t.strip() for t in env_val.split(',') if t.strip()])
    # De-dup while preserving order
    seen = set(); out = []
    for t in lst:
        tl = t.strip().lower()
        if tl and tl not in seen:
            seen.add(tl); out.append(tl)
    return out

get_layer_bonuses(repo: Optional[str]) -> Dict[str, Dict[str, float]]:
    return _layer_bonuses_cfg(repo or "")

load_repos() -> Dict[str, Any]:
    """
    Load repository configuration.

    Format:
      {
        "default_repo": "example-1",
        "repos": [
          {"name": "example-1", "path": "/abs/path/example-1",
           "keywords": ["auth", "backend"],
           "path_boosts": ["src/server/", "api/"],
           "layer_bonuses": {"server": {"kernel": 0.10}, "ui": {"ui": 0.12}}
          },
          {"name": "example-2", "path": ["/abs/path/example-2", "/abs/alt"]}
        ]
      }

    Fallbacks (if repos.json missing):
      - If REPO and REPO_PATH env set, synthesize a single-repo config.
      - Else raise a clear error when a function requires config.
    """
    global _CACHE
    if "config" in _CACHE:
        return _CACHE["config"]

    p = _repos_file_path()
    if p.exists():
        try:
            data = json.loads(p.read_text())
            if isinstance(data, dict) and isinstance(data.get("repos"), list):
                _CACHE["config"] = data
                return data
        except Exception:
            pass

    # Fallback to environment-only single repo
    env_repo = (os.getenv("REPO") or "default").strip()
    env_path = os.getenv("REPO_PATH") or os.getenv(f"REPO_{env_repo.upper()}_PATH")
    if env_path:
        cfg = {
            "default_repo": env_repo,
            "repos": [
                {"name": env_repo, "path": env_path}
            ]
        }
        _CACHE["config"] = cfg
        return cfg

    # Last resort minimal placeholder (no repos) to allow help text rendering
    cfg = {"default_repo": None, "repos": []}
    _CACHE["config"] = cfg
    return cfg

list_repos() -> List[str]:
    cfg = load_repos()
    return [str(r.get("name")) for r in cfg.get("repos", []) if r.get("name")]

get_default_repo() -> str:
    cfg = load_repos()
    # 1) explicit default
    if cfg.get("default_repo"):
        return str(cfg["default_repo"]).strip()
    # 2) first repo
    repos = cfg.get("repos", [])
    if repos:
        return str(repos[0].get("name"))
    # 3) env REPO or sentinel
    return (os.getenv("REPO") or "default").strip()

_find_repo(name: str) -> Optional[Dict[str, Any]]:
    name_low = (name or "").strip().lower()
    if not name_low:
        return None
    for r in load_repos().get("repos", []):
        if (r.get("name") or "").strip().lower() == name_low:
            return r
    return None

get_repo_paths(name: str) -> List[str]:
    r = _find_repo(name)
    if not r:
        raise ValueError(f"Unknown repo: {name}. Known: {', '.join(list_repos()) or '[]'}")
    p = r.get("path")
    if isinstance(p, list):
        return [str(Path(x).expanduser()) for x in p]
    if isinstance(p, str):
        return [str(Path(p).expanduser())]
    raise ValueError(f"Repo `{name}` missing 'path' in repos.json")

path_boosts(name: str) -> List[str]:
    r = _find_repo(name)
    if not r:
        return []
    lst = r.get("path_boosts") or []
    return [str(x) for x in lst if isinstance(x, str)]

layer_bonuses(name: str) -> Dict[str, Dict[str, float]]:
    r = _find_repo(name)
    if not r:
        return {}
    lb = r.get("layer_bonuses") or {}
    # Normalize numeric values
    out: Dict[str, Dict[str, float]] = {}
    for intent, d in (lb.items() if isinstance(lb, dict) else []):
        if not isinstance(d, dict):
            continue
        out[intent] = {k: float(v) for k, v in d.items() if isinstance(v, (int, float))}
    return out

choose_repo_from_query(query: str, default: Optional[str] = None) -> str:
    q = (query or "").lower().strip()
    # Allow explicit prefix: "<name>: question"
    if ":" in q:
        cand, _ = q.split(":", 1)
        cand = cand.strip()
        if cand in [r.lower() for r in list_repos()]:
            return cand
    # Keyword voting across repos
    best = None
    best_hits = 0
    for name in list_repos():
        hits = 0
        for kw in get_repo_keywords(name):
            if kw and kw in q:
                hits += 1
        if hits > best_hits:
            best = name
            best_hits = hits
    if best:
        return best
    return (default or get_default_repo())

