route_repo(query: str, default_repo: str | None = None) -> str:
    """Route to a repo using repos.json config and lightweight prefixing.

    - Supports explicit prefix: "<name>: question"
    - Falls back to keyword voting as configured in repos.json
    - Defaults to configured default_repo (repos.json) or env REPO
    """
    try:
        # Prefer config-driven choice (handles prefixes + keywords)
        return choose_repo_from_query(query, default=(default_repo or get_default_repo()))
    except Exception:
        # Very safe fallback
        q = (query or '').lower().strip()
        if ':' in q:
            cand, _ = q.split(':', 1)
            cand = cand.strip()
            if cand:
                return cand
        return (default_repo or os.getenv('REPO', 'project') or 'project').strip()
search_routed(query: str, repo_override: str | None = None, final_k: int = 10):
    repo = (repo_override or route_repo(query, default_repo=os.getenv('REPO', 'project')) or os.getenv('REPO', 'project')).strip()
    return search(query, repo=repo, final_k=final_k)

# Multi-query expansion (cheap) and routed search

expand_queries(query: str, m: int = 4) -> list[str]:
    # Fast path: no expansion requested
    if m <= 1:
        return [query]
    try:
        sys = "Rewrite a developer query into multiple search-friendly variants without changing meaning."
        user = f"Count: {m}\nQuery: {query}\nOutput one variant per line, no numbering."
        text, _ = generate_text(user_input=user, system_instructions=sys, reasoning_effort=None)
        lines = [ln.strip('- ').strip() for ln in (text or '').splitlines() if ln.strip()]
        uniq = []
        for ln in lines:
            if ln and ln not in uniq:
                uniq.append(ln)
        return (uniq or [query])[:m]
    except Exception:
        return [query]

watch_mode():
    """Watch for file changes and re-run eval."""
    print("⏱ Watch mode: monitoring for changes...")
    print(f"   Watching: {GOLDEN_PATH}, hybrid_search.py, langgraph_app.py")

    files_to_watch = [
        GOLDEN_PATH,
        "hybrid_search.py",
        "langgraph_app.py",
        "index_repo.py",
        "rerank.py"
    ]

    last_mtimes = {}
    for fp in files_to_watch:
        if os.path.exists(fp):
            last_mtimes[fp] = os.path.getmtime(fp)

    while True:
        time.sleep(5)
        changed = False
        for fp in files_to_watch:
            if not os.path.exists(fp):
                continue
            mtime = os.path.getmtime(fp)
            if fp not in last_mtimes or mtime > last_mtimes[fp]:
                print(f"\n🔄 Change detected: {fp}")
                last_mtimes[fp] = mtime
                changed = True

        if changed:
            print("\n" + "="*60)
            print("Running eval...")
            print("="*60)
            results = run_eval_with_results()
            if "error" in results:
                print(f"Error: {results['error']}")
            else:
                print(json.dumps({
                    "top1_accuracy": results["top1_accuracy"],
                    "topk_accuracy": results["topk_accuracy"],
                    "total": results["total"],
                    "duration_secs": results["duration_secs"]
                }, indent=2))

