enrich(file_path: str, lang: str, code: str) -> dict:
    prompt = PROMPT_TMPL.format(file=file_path, lang=lang, code=(code or '')[:4000])

    if ENRICH_BACKEND == "mlx":
        # MLX backend (Apple Silicon, fast)
        try:
            from mlx_lm import generate
            model, tokenizer = _get_mlx_model()
            txt = generate(model, tokenizer, prompt=prompt, max_tokens=150, verbose=False)
        except Exception as e:
            return {"summary": f"MLX error: {str(e)[:100]}", "keywords": []}
    elif ENRICH_BACKEND == "ollama":
        # Ollama backend (fallback)
        import requests
        try:
            resp = requests.post(
                OLLAMA_URL,
                json={
                    "model": OLLAMA_MODEL,
                    "prompt": prompt,
                    "stream": False,
                    "options": {"temperature": 0.1, "num_ctx": 4096},
                },
                timeout=10,
            )
            resp.raise_for_status()
            txt = resp.json().get("response", "{}")
        except Exception as e:
            return {"summary": f"Ollama error: {str(e)[:100]}", "keywords": []}
    else:
        # OpenAI backend (cloud)
        try:
            from openai import OpenAI
            client = OpenAI(api_key=OPENAI_API_KEY, base_url=OPENAI_BASE_URL) if OPENAI_BASE_URL else OpenAI(api_key=OPENAI_API_KEY)
            msgs = [
                {"role": "system", "content": SYSTEM},
                {"role": "user", "content": prompt},
            ]
            r = client.chat.completions.create(model=OPENAI_MODEL, messages=msgs, temperature=0.1, max_tokens=200)
            txt = r.choices[0].message.content or "{}"
        except Exception as e:
            return {"summary": f"OpenAI error: {str(e)[:100]}", "keywords": []}

    # Parse JSON response
    # Parse JSON response; if model returned plain text, fallback to capturing tokens
    try:
        data = json.loads(txt)
        if isinstance(data, dict):
            kws = data.get("keywords") or []
            if isinstance(kws, str):
                try:
                    kws = json.loads(kws)
                except Exception:
                    kws = [w.strip() for w in kws.split(',') if w.strip()]
            return {"summary": data.get("summary", ""), "keywords": kws}
    except Exception:
        pass
    # heuristic fallback: extract potential keywords from text
    import re as _re
    toks = [t.lower() for t in _re.findall(r"\b[a-zA-Z_][a-zA-Z0-9_]{2,}\b", txt or "")][:15]
    return {"summary": (txt or "")[:300], "keywords": toks}

_weights(wl: Dict[str, Number]) -> Dict[str, Number]:
    W_GEN = wl["R"] * (wl["Tin"] + wl["Tout"])
    W_EMB = wl["R"] * wl["E_tokens"]
    W_RR = wl["R"] * wl["K_tokens"]
    total = W_GEN + W_EMB + W_RR
    if total <= 0:
        return dict(Wg=1 / 3, We=1 / 3, Wr=1 / 3)
    return dict(Wg=W_GEN / total, We=W_EMB / total, Wr=W_RR / total)

_allowed_set(policy: Dict[str, Any]) -> set:
    providers = policy.get("providers_allowed") or []
    return set([p.lower() for p in providers if isinstance(p, str)])

_meets_policy_maps(candidate: Dict[str, Any], policy: Dict[str, Any]) -> bool:
    regions_allowed = policy.get("regions_allowed")
    compliance = policy.get("compliance")
    for comp in ("GEN", "EMB", "RERANK"):
        row = candidate.get(comp, {})
        if not row:
            return False
        region = row.get("region")
        comp_flags = set(row.get("compliance", []) or [])
        if regions_allowed and region and region not in regions_allowed:
            return False
        if compliance and comp_flags and not comp_flags.issuperset(set(compliance)):
            return False
    return True

_infer_quality_score(row: Dict[str, Any], comp_type: str) -> Number:
    """Heuristic quality when not provided in prices.json.
    Tries to be sensible for performance mode ranking.
    """
    prov = (row.get("provider") or "").lower()
    fam = (row.get("family") or "").lower()
    model = (row.get("model") or "").lower()
    c = comp_type.upper()

    if c == "GEN":
        # OpenAI / o-series / GPT-4 family hierarchy
        if prov == "openai":
            if model.startswith("o1"):
                return 0.95
            if model.startswith("o3"):
                return 0.90
            if "gpt-4o" in model and "mini" not in model:
                return 0.88
            if "gpt-4-turbo" in model:
                return 0.86
            if model.startswith("gpt-4"):
                return 0.85
            if "gpt-4o-mini" in model:
                return 0.78
        if prov == "anthropic":
            if "opus" in model:
                return 0.98
            if "3-5-sonnet" in model or "3.5-sonnet" in model or "sonnet" in model:
                return 0.92
            if "3-5-haiku" in model or "3.5-haiku" in model or "haiku" in model:
                return 0.82
        if prov == "google":
            if "gemini-1.5-pro" in model:
                return 0.87
            if "gemini-1.5-flash" in model:
                return 0.78
        if prov == "meta" and "70b" in model:
            return 0.83
        if prov == "mistral" and "large" in model:
            return 0.82
        if prov == "cohere" and "command-r-plus" in model:
            return 0.84
        # local or unknown
        return 0.72 if prov != "local" else 0.60

    if c == "EMB":
        if prov == "openai":
            if "3-large" in model:
                return 0.90
            if "3-small" in model:
                return 0.82
        if prov == "voyage":
            return 0.86
        if prov == "cohere":
            return 0.78
        return 0.70 if prov != "local" else 0.60

    if c == "RERANK":
        if prov == "cohere" and ("3.5" in model or "rerank-3.5" in model):
            return 0.90
        if prov in ("hf", "local") and ("bge-reranker" in model or "reranker" in model):
            return 0.82
        return 0.75 if prov != "local" else 0.65

    return 0.5

_component_rows(
    comp_type: str,
    ALLOW: set,
    prices: Dict[str, Any],
    include_local: bool = False,
    use_heuristics: bool = False,
) -> List[Dict[str, Any]]:
    rows: List[Dict[str, Any]] = []
    models = prices.get("models") or []
    comp = comp_type.upper()

    for m in models:
        prov = (m.get("provider") or "").lower()
        if prov == "local":
            continue
        if ALLOW and prov not in ALLOW:
            continue
        unit = (m.get("unit") or "")
        if comp == "GEN":
            if unit == "1k_tokens" and (
                _safe_num(m.get("input_per_1k")) > 0 or _safe_num(m.get("output_per_1k")) > 0
            ):
                rows.append(_decorate_row(m, comp, use_heuristics))
        elif comp == "EMB":
            if _safe_num(m.get("embed_per_1k")) > 0:
                rows.append(_decorate_row(m, comp, use_heuristics))
        elif comp == "RERANK":
            if _safe_num(m.get("rerank_per_1k")) > 0 or unit == "request":
                rows.append(_decorate_row(m, comp, use_heuristics))

    if include_local and ((not ALLOW) or ("local" in ALLOW)):
        local_stub = dict(
            provider="local",
            model="local",
            unit="request",
            quality_score=0.5,
            latency_p95_ms=None,
            throughput_qps=None,
        )
        rows.insert(0, _decorate_row(local_stub, comp, use_heuristics))

    rows.sort(key=lambda r: r["quality_score"], reverse=True)
    cap = 4 if comp == "GEN" else 3
    return rows[:cap]

_pair_limited(GENs, EMBs, RRs, limit: int = 60) -> List[Dict[str, Any]]:
    out: List[Dict[str, Any]] = []
    for g in GENs:
        for e in EMBs:
            for r in RRs:
                out.append({"GEN": g, "EMB": e, "RERANK": r})
                if len(out) >= limit:
                    return out
    return out

_valid_pipeline(c: Dict[str, Any]) -> bool:
    g = c.get("GEN")
    return bool(g and g.get("provider") and g.get("model"))

_meets_slos(c: Dict[str, Any], slo: Dict[str, Any]) -> bool:
    target_ms = slo.get("latency_target_ms")
    min_qps = slo.get("min_qps")
    if target_ms is None and min_qps is None:
        return True
    for comp in ("GEN", "EMB", "RERANK"):
        row = c.get(comp, {})
        if target_ms is not None and row.get("latency_p95_ms") is not None:
            if _safe_num(row.get("latency_p95_ms")) > float(target_ms):
                return False
        if min_qps is not None and row.get("throughput_qps") is not None:
            if _safe_num(row.get("throughput_qps")) < float(min_qps):
                return False
    return True

_monthly_cost(c: Dict[str, Any], wl: Dict[str, Number]) -> Number:
    R = wl["R"]
    Tin = wl["Tin"]
    Tout = wl["Tout"]
    E_tokens = wl["E_tokens"]
    K_tokens = wl["K_tokens"]
    P = 30.0

    def gen_cost(row):
        if row.get("provider") == "local":
            return 0.0
        inp = _safe_num(row.get("input_per_1k"))
        out = _safe_num(row.get("output_per_1k"))
        return (Tin / 1000.0) * inp + (Tout / 1000.0) * out

    def emb_cost(row):
        if row.get("provider") == "local":
            return 0.0
        emb = _safe_num(row.get("embed_per_1k"))
        return (E_tokens / 1000.0) * emb

    def rr_cost(row):
        if row.get("provider") == "local":
            return 0.0
        rrk = row.get("rerank_per_1k")
        if rrk is not None:
            return (K_tokens / 1000.0) * _safe_num(rrk)
        return _safe_num(row.get("per_request"))

    per_req = gen_cost(c["GEN"]) + emb_cost(c["EMB"]) + rr_cost(c["RERANK"])
    return per_req * R * P

_select_cost(C: List[Dict[str, Any]], B: Optional[Number]) -> Dict[str, Any]:
    if B is not None:
        feasible = [c for c in C if c["monthly"] <= B]
        if feasible:
            return min(feasible, key=lambda x: x["monthly"])
    return min(C, key=lambda x: x["monthly"])

_select_performance(C: List[Dict[str, Any]]) -> Dict[str, Any]:
    # Maximize utility. If tie, prefer higher sum of component qualities; then tie-break by min cost.
    bestU = max(c["utility"] for c in C)
    top = [c for c in C if c["utility"] == bestU]
    if len(top) <= 1:
        return top[0]
    def qsum(c: Dict[str, Any]) -> Number:
        return _safe_num(c["GEN"].get("quality_score"), 0.0) + _safe_num(c["EMB"].get("quality_score"), 0.0) + _safe_num(c["RERANK"].get("quality_score"), 0.0)
    bestQ = max(qsum(c) for c in top)
    top2 = [c for c in top if qsum(c) == bestQ]
    return min(top2, key=lambda x: x["monthly"])

_select_balanced(C: List[Dict[str, Any]], B: Optional[Number]) -> Dict[str, Any]:
    if B is not None:
        feasible = [c for c in C if c["monthly"] <= B]
        if feasible:
            bestU = max(c["utility"] for c in feasible)
            top = [c for c in feasible if c["utility"] == bestU]
            return min(top, key=lambda x: x["monthly"])
        lam = 1.0 / (B if B and B > 0 else 1.0)
        def score(c):
            return c["utility"] - lam * (c["monthly"] - B)

        return max(C, key=score)
    return _select_performance(C)

autoprofile(request: Dict[str, Any], prices: Dict[str, Any]) -> Tuple[Dict[str, str], Dict[str, Any]]:
    hw = request.get("hardware", {})
    rt = hw.get("runtimes", {}) or {}
    policy = request.get("policy", {}) or {}
    wl = _normalize_workload(request.get("workload", {}) or {})
    obj = request.get("objective", {}) or {}
    defaults = request.get("defaults", {}) or {}

    ALLOW = _allowed_set(policy)
    local_cap = _any_true(rt, ["cuda", "ollama", "coreml", "openvino", "vpu", "npu", "mps"])
    B = obj.get("monthly_budget_usd")
    mode = (obj.get("mode") or "balanced").lower()
    slo = {"latency_target_ms": obj.get("latency_target_ms"), "min_qps": obj.get("min_qps")}

    W = _weights(wl)
    tuning = request.get("tuning", {}) or {}
    use_heuristics = bool(tuning.get("use_heuristic_quality"))

    # Diagnostics: available rows under current provider policy
    diag = {
        "providers_allowed": sorted(list(ALLOW)) if ALLOW else None,
        "local_cap": bool(local_cap),
        "use_heuristic_quality": use_heuristics,
        "rows": {
            "gen": len(_component_rows("GEN", ALLOW, prices, include_local=False, use_heuristics=use_heuristics)),
            "emb": len(_component_rows("EMB", ALLOW, prices, include_local=local_cap, use_heuristics=use_heuristics)),
            "rerank": len(_component_rows("RERANK", ALLOW, prices, include_local=local_cap, use_heuristics=use_heuristics)),
        }
    }

    def build_candidates(AL: set) -> List[Dict[str, Any]]:
        C: List[Dict[str, Any]] = []
        if local_cap:
            gen_local = defaults.get("gen_model") if _looks_local(defaults.get("gen_model")) else None
            top_cloud_gen = _component_rows("GEN", AL, prices, include_local=False, use_heuristics=use_heuristics)
            GENs = [{"provider": "local", "model": gen_local}] if gen_local else top_cloud_gen
            EMBs = _component_rows("EMB", AL, prices, include_local=True, use_heuristics=use_heuristics)
            RRs = _component_rows("RERANK", AL, prices, include_local=True, use_heuristics=use_heuristics)
            C.extend(_pair_limited(GENs, EMBs, RRs, limit=60))
        GENs = _component_rows("GEN", AL, prices, include_local=False, use_heuristics=use_heuristics)
        EMBs = _component_rows("EMB", AL, prices, include_local=local_cap, use_heuristics=use_heuristics)
        RRs = _component_rows("RERANK", AL, prices, include_local=local_cap, use_heuristics=use_heuristics)
        C.extend(_pair_limited(GENs, EMBs, RRs, limit=60))
        C = [c for c in C if _valid_pipeline(c)]
        C = [c for c in C if _meets_slos(c, slo)]
        try:
            C = [c for c in C if _meets_policy_maps(c, policy)]
        except Exception:
            pass
        return C

    C = build_candidates(ALLOW)

    # Fallback: if providers_allowed is non-empty and produced no candidates, relax provider filter once.
    relaxed = False
    if not C and ALLOW:
        C = build_candidates(set())
        relaxed = bool(C)

    if not C:
        return {}, {"error": "no_viable_candidate", "why": "after building/filters", "providers_allowed": list(ALLOW), "diag": diag}

    for c in C:
        c["monthly"] = _monthly_cost(c, wl)
        c["utility"] = _utility(c, W, defaults, slo)

    if mode == "cost":
        winner = _select_cost(C, B)
    elif mode == "performance":
        winner = _select_performance(C)
    else:
        winner = _select_balanced(C, B)

    # Recommend MQ_REWRITES when not provided (derive from objective + budget + workload size)
    def recommend_mq(mode: str, B: Optional[Number], wl: Dict[str, Number]) -> int:
        tin = wl.get("Tin", 0.0); tout = wl.get("Tout", 0.0)
        size = tin + tout
        B = (B or 0.0)
        if mode == "performance":
            if B >= 1000 or size >= 50000:  # heavy usage or large prompts/answers
                return 6
            if B >= 100 or size >= 5000:
                return 4
            return 3
        if mode == "cost":
            if B < 10 and size < 2000:
                return 1
            return 2
        # balanced
        if B >= 50 or size >= 5000:
            return 4
        return 3

    mq = int(wl["MQ"]) if wl["MQ"] > 0 else recommend_mq(mode, B, wl)

    env: Dict[str, str] = {
        "HYDRATION_MODE": "lazy",
        "MQ_REWRITES": str(mq),
        "GEN_MODEL": winner["GEN"]["model"],
        "EMBEDDING_TYPE": "local" if winner["EMB"]["provider"] == "local" else winner["EMB"]["provider"],
        "RERANK_BACKEND": "local" if winner["RERANK"]["provider"] == "local" else winner["RERANK"]["provider"],
    }
    if env["RERANK_BACKEND"] == "cohere":
        env["COHERE_RERANK_MODEL"] = winner["RERANK"]["model"]

    reason = {
        "objective": mode,
        "budget": B,
        "workload": wl,
        "weights": W,
        "candidates_total": len(C),
        "selected": {
            "gen": winner["GEN"],
            "embed": winner["EMB"],
            "rerank": winner["RERANK"],
            "monthly": winner["monthly"],
            "utility": winner["utility"],
        },
        "policy_relaxed": relaxed,
        "diag": diag,
    }
    return env, reason

recreate_collection_safe(self, *args, **kwargs):
    try:
        return _orig_recreate(self, *args, **kwargs)
    except UnexpectedResponse as e:
        # Some servers return 404 on delete step inside recreate
        if getattr(e, "status_code", None) == 404:
            name, vectors_config = _extract_args(*args, **kwargs)
            return self.create_collection(collection_name=name, vectors_config=vectors_config)
        raise
    except Exception:
        # Very defensive fallback: try delete (ignore errors), then create
        name, vectors_config = _extract_args(*args, **kwargs)
        try:
            try:
                self.delete_collection(name)
            except Exception:
                pass
            return self.create_collection(collection_name=name, vectors_config=vectors_config)
        except Exception:
            raise


QdrantClient.recreate_collection = recreate_collection_safe

