lang_from_path(path:str)->Optional[str]:
    _, ext = os.path.splitext(path)
    return LANG_MAP.get(ext.lower())
nonws_len(s:str)->int:
    return len(re.sub(r"\s+", "", s))
extract_imports(src:str, lang:str)->List[str]:
    try:
        if _ts_get_parser is None:
            raise RuntimeError("tree_sitter_languages not available")
        parser = _ts_get_parser(lang)
        tree = parser.parse(bytes(src, "utf-8"))
        imports = []
        def walk(n):
            if n.type in IMPORT_NODES.get(lang, set()):
                imports.append(src[n.start_byte:n.end_byte])
            for c in n.children:
                walk(c)
        walk(tree.root_node)
        return imports
    except Exception:
        if lang == "python":
            return re.findall(r"^(?:from\s+[^\n]+|import\s+[^\n]+)$", src, flags=re.M)
        if lang in {"javascript","typescript"}:
            return re.findall(r"^import\s+[^\n]+;$", src, flags=re.M)
        return []

greedy_fallback(src:str, fpath:str, lang:str, target:int)->List[Dict]:
    sep = r"(?:\nclass\s+|\ndef\s+)" if lang=="python" else r"(?:\nclass\s+|\nfunction\s+)"
    parts = re.split(sep, src)
    if len(parts) < 2:
        out, cur, acc = [], [], 0
        for line in src.splitlines(True):
            cur.append(line); acc += nonws_len(line)
            if acc >= target:
                out.append("".join(cur)); cur, acc = [], 0
        if cur: out.append("".join(cur))
        return [{
            "id": hashlib.md5((fpath+str(i)+s[:80]).encode()).hexdigest()[:12],
            "file_path": fpath, "language": lang, "type":"blob","name":None,
            "start_line": 1, "end_line": s.count("\n")+1, "imports": extract_imports(src, lang), "code": s
        } for i,s in enumerate(out)]
    else:
        rejoined, buf, acc = [], [], 0
        for p in parts:
            if acc + nonws_len(p) > target and buf:
                s = "".join(buf); rejoined.append(s); buf, acc = [], 0
            buf.append(p); acc += nonws_len(p)
        if buf: rejoined.append("".join(buf))
        return [{
            "id": hashlib.md5((fpath+str(i)+s[:80]).encode()).hexdigest()[:12],
            "file_path": fpath, "language": lang, "type":"section","name":None,
            "start_line": 1, "end_line": s.count("\n")+1, "imports": extract_imports(s, lang), "code": s
        } for i,s in enumerate(rejoined)]

collect_files(roots:List[str])->List[str]:
    import fnmatch
    out=[]
    skip_dirs = {".git","node_modules",".venv","venv","dist","build","__pycache__",".next",".turbo",".parcel-cache",".pytest_cache","vendor","third_party",".bundle","Pods"}
    exclude_patterns = []
    for root in roots:
        parent_dir = os.path.dirname(root) if os.path.isfile(root) else root
        exclude_file = os.path.join(parent_dir, 'data', 'exclude_globs.txt')
        if os.path.exists(exclude_file):
            try:
                with open(exclude_file, 'r') as f:
                    patterns = [line.strip() for line in f if line.strip() and not line.startswith('#')]
                    exclude_patterns.extend(patterns)
            except Exception:
                pass
    for root in roots:
        for dp, dns, fns in os.walk(root):
            dns[:] = [d for d in dns if d not in skip_dirs and not d.startswith('.venv') and not d.startswith('venv')]
            for fn in fns:
                p = os.path.join(dp, fn)
                skip = False
                for pattern in exclude_patterns:
                    if fnmatch.fnmatch(p, pattern) or fnmatch.fnmatch(os.path.relpath(p, root), pattern):
                        skip = True
                        break
                if not skip and lang_from_path(p):
                    out.append(p)
    return out

chunk_code(src:str, fpath:str, lang:str, target:int=900)->List[Dict]:
    try:
        if _ts_get_parser is None:
            raise RuntimeError("tree_sitter_languages not available")
        parser = _ts_get_parser(lang)
        tree = parser.parse(bytes(src, "utf-8"))
        wanted = FUNC_NODES.get(lang, set())
        nodes = []
        stack = [tree.root_node]
        while stack:
            n = stack.pop()
            if n.type in wanted:
                nodes.append(n)
            stack.extend(n.children)
        if not nodes:
            return greedy_fallback(src, fpath, lang, target)
        chunks: List[Dict] = []
        all_lines = src.splitlines()
        for i, n in enumerate(nodes):
            text = src[n.start_byte:n.end_byte]
            if nonws_len(text) > target:
                for j, sub in enumerate(greedy_fallback(text, fpath, lang, target)):
                    sub["id"] = hashlib.md5((fpath+f"/{i}:{j}"+sub["code"][:80]).encode()).hexdigest()[:12]
                    sub["start_line"] = n.start_point[0]+1
                    sub["end_line"] = sub["start_line"] + sub["code"].count("\n")
                    chunks.append(sub)
            else:
                name = _guess_name(lang, text)
                start_line = n.start_point[0] + 1
                end_line = n.end_point[0] + 1
                actual_start = max(1, start_line - OVERLAP_LINES) if OVERLAP_LINES > 0 else start_line
                chunk_text = "\n".join(all_lines[actual_start-1:end_line])
                chunks.append({
                    "id": hashlib.md5((fpath+str(i)+text[:80]).encode()).hexdigest()[:12],
                    "file_path": fpath,
                    "language": lang,
                    "type": "unit",
                    "name": name,
                    "start_line": actual_start,
                    "end_line": end_line,
                    "imports": extract_imports(src, lang),
                    "code": chunk_text,
                })
        return chunks
    except Exception:
        return greedy_fallback(src, fpath, lang, target)

EmbeddingCache:
    def __init__(self, outdir: str):
        os.makedirs(outdir, exist_ok=True)
        self.path = os.path.join(outdir, "embed_cache.jsonl")
        self.cache = {}
        if os.path.exists(self.path):
            with open(self.path, "r", encoding="utf-8") as f:
                for line in f:
                    try:
                        o = json.loads(line)
                        self.cache[o["hash"]] = o["vec"]
                    except Exception:
                        pass

    def get(self, h: str):
        return self.cache.get(h)

    def put(self, h: str, v):
        self.cache[h] = v

    def save(self):
        with open(self.path, "w", encoding="utf-8") as f:
            for h, v in self.cache.items():
                f.write(json.dumps({"hash": h, "vec": v}) + "\n")

    def prune(self, valid_hashes: set):
        before = len(self.cache)
        self.cache = {h: v for h, v in self.cache.items() if h in valid_hashes}
        after = len(self.cache)
        pruned = before - after
        if pruned > 0:
            self.save()
        return pruned

    def embed_texts(self, client, texts, hashes, model="text-embedding-3-large", batch=64):
        embs = [None] * len(texts)
        to_embed, idx_map = [], []
        for i, (t, h) in enumerate(zip(texts, hashes)):
            v = self.get(h)
            if v is None:
                idx_map.append(i)
                to_embed.append(t)
            else:
                embs[i] = v
        enc = tiktoken.get_encoding('cl100k_base')

        def _clip_for_openai(text: str, max_tokens: int = 8000) -> str:
            toks = enc.encode(text)
            if len(toks) <= max_tokens:
                return text
            return enc.decode(toks[:max_tokens])

        for i in range(0, len(to_embed), batch):
            sub = [_clip_for_openai(t) for t in to_embed[i:i + batch]]
            r = client.embeddings.create(model=model, input=sub)
            for j, d in enumerate(r.data):
                orig = idx_map[i + j]
                vec = d.embedding
                embs[orig] = vec
                self.put(hashes[orig], vec)
        return embs

trigger(domain: str) -> dict:
    s = find_site(domain)
    if not s:
        return {"domain": domain, "status": "not_found"}
    sid = s.get("id")
    if not sid:
        return {"domain": domain, "status": "no_site_id"}
    try:
        b = api(f"/sites/{sid}/builds", "POST", {})
        return {"domain": domain, "status": "triggered", "site_id": sid, "build_id": b.get("id")}
    except Exception as e:
        return {"domain": domain, "status": "error", "error": str(e)}

#!/usr/bin/env python3
import os, sys, re

SCAN_ALL = os.getenv("SCAN_ALL", "0").lower() in {"1","true","yes"}
ROOTS = [os.path.abspath(os.path.join(os.path.dirname(__file__), '..'))]
if SCAN_ALL:
    ROOTS += [
        os.getenv('PROJECT_PATH', '/abs/path/to/project'),
        os.getenv('project_PATH', '/abs/path/to/project'),
    ]

BAD_PATTERNS = [
    r"\bChatCompletion\b",
    r"\bclient\.chat\.completions\b",
    r"\bassistants?\.v1\b",
    r"\bgpt-3\.5\b",
    r"\bgpt-4(?!\.1|o)\b",
    r"\bgpt-4o(-mini)?\b",
    r"\btext-embedding-ada\b",
    r"\btext-embedding-00[23]\b",
]
ALLOWLIST_FILES = {
    # add filenames you want ignored (e.g., historical docs)
}

SKIP_DIRS = {".git", ".venv", "venv", "node_modules", "dist", "build", "vendor", "third_party", "site-packages", "__pycache__"}

scan_file(path: str) -> list[str]:
    try:
        with open(path, "r", errors="ignore") as f:
            s = f.read()
    except Exception:
        return []
    hits = []
    for pat in BAD_PATTERNS:
        if re.search(pat, s):
            hits.append(pat)
    return hits

main() -> int:
    offenders = []
    for root in ROOTS:
        if not os.path.isdir(root):
            continue
        for base, dirs, files in os.walk(root):
            # prune skip dirs
            dirs[:] = [d for d in dirs if d not in SKIP_DIRS and not d.startswith('.')]
            for name in files:
                if name in ALLOWLIST_FILES:
                    continue
                # Scan code files only (skip docs like .md)
                if not any(name.endswith(ext) for ext in (".py", ".ts", ".tsx", ".js", ".rb")):
                    continue
                path = os.path.join(base, name)
                # skip this guard file and sitecustomize self-detection
                if path.endswith("scripts/guard_legacy_api.py") or path.endswith("sitecustomize.py"):
                    continue
                hits = scan_file(path)
                if hits:
                    offenders.append((path, hits))
    if offenders:
        print("\u274c Legacy APIs/models detected:")
        for p, pats in offenders:
            print(f"- {p}")
            for pat in pats:
                print(f"    \u21b3 {pat}")
        print("\nAction: replace Chat Completions with Responses API calls; update model pins (e.g., gpt-4o-mini-latest or a dated pin).")
        print("Docs:")
        print("  https://openai.com/index/new-tools-and-features-in-the-responses-api/")
        print("  https://openai.com/index/introducing-upgrades-to-codex/")
        return 2
    print("\u2713 No legacy APIs/models detected.")
    return 0


if __name__ == "__main__":
    raise SystemExit(main())

count_tokens(text: str, model: str = "gpt-4o") -> int:
    """Count tokens precisely or estimate"""
    if HAS_TIKTOKEN:
        try:
            encoding = tiktoken.encoding_for_model(model)
            return len(encoding.encode(text))
        except:
            pass
    return len(text) // 4


