write_repos_json(rag_root: Path, name: str, code_path: Path) -> Path:
    p = os.getenv('REPOS_FILE') or str(rag_root / 'repos.json')
    repos_path = Path(p)
    cfg = {'default_repo': name, 'repos': []}
    if repos_path.exists():
        try:
            cfg = json.loads(repos_path.read_text())
            if not isinstance(cfg, dict):
                cfg = {'default_repo': name, 'repos': []}
        except Exception:
            cfg = {'default_repo': name, 'repos': []}
    # Update or append
    repos = cfg.get('repos') or []
    found = False
    for r in repos:
        if (r.get('name') or '').strip().lower() == name.lower():
            r['path'] = str(code_path)
            found = True
            break
    if not found:
        repos.append({'name': name, 'path': str(code_path), 'keywords': [], 'path_boosts': [], 'layer_bonuses': {}})
    cfg['repos'] = repos
    # Ask to set default
    if Confirm.ask(f"Make [bold]{name}[/bold] the default repo?", default=True):
        cfg['default_repo'] = name
    repos_path.write_text(json.dumps(cfg, indent=2))
    return repos_path

_claude_config_path() -> Path | None:
    sysname = platform.system().lower()
    home = Path.home()
    if 'darwin' in sysname or 'mac' in sysname:
        return (home / 'Library' / 'Application Support' / 'Claude' / 'claude_desktop_config.json')
    if 'linux' in sysname:
        return (home / '.config' / 'Claude' / 'claude_desktop_config.json')
    if 'windows' in sysname or 'win' in sysname:
        appdata = os.getenv('APPDATA')
        if appdata:
            return Path(appdata) / 'Claude' / 'claude_desktop_config.json'
    return None

claude_register(rag_root: Path, progress: Progress, task_id) -> None:
    cfgp = _claude_config_path()
    if not cfgp:
        progress.update(task_id, description='Claude config path not found (skip)')
        time.sleep(0.3)
        return
    cfgp.parent.mkdir(parents=True, exist_ok=True)
    py = _venv_python(rag_root)
    server = rag_root / 'mcp_server.py'
    # Load existing
    data = {}
    if cfgp.exists():
        try:
            data = json.loads(cfgp.read_text())
        except Exception:
            data = {}
        # backup
        bak = cfgp.with_suffix(cfgp.suffix + f'.bak.{time.strftime("%Y%m%d-%H%M%S")}')
        bak.write_text(json.dumps(data, indent=2))
    # Merge entry
    ms = data.get('mcpServers') or {}
    ms['rag-service'] = {
        'command': str(py),
        'args': [str(server)],
        'env': {
            'OPENAI_API_KEY': os.getenv('OPENAI_API_KEY', '')
        }
    }
    data['mcpServers'] = ms
    progress.update(task_id, description='Writing Claude config')
    cfgp.write_text(json.dumps(data, indent=2))

measure_traditional_tokens(question: str, repo: str, max_files: int = 10):
    """
    Simulate traditional approach: grep for keywords, read full files.
    This is what you'd do WITHOUT RAG.
    """
    repo_paths = {
        'project': os.getenv('PROJECT_PATH', '/abs/path/to/project'),
        'faxbot': os.getenv('FAXBOT_PATH', '/abs/path/to/faxbot')
    }

    repo_path = repo_paths.get(repo)
    if not repo_path or not os.path.exists(repo_path):
        return {'approach': 'Traditional', 'error': f'Repo not found: {repo_path}'}

    # Extract keywords from question (simulate what a human would grep for)
    keywords = [w.lower() for w in question.split() if len(w) > 3][:5]

    # Find files containing keywords
    combined_text = ""
    matched_files = []

    for py_file in Path(repo_path).rglob('*.py'):
        if 'node_modules' in str(py_file) or '.venv' in str(py_file):
            continue

        try:
            content = py_file.read_text(errors='ignore')

            # If any keyword appears, a human would likely read this whole file
            if any(kw in content.lower() for kw in keywords):
                matched_files.append(str(py_file))
                combined_text += f"\n{'='*60}\nFile: {py_file}\n{'='*60}\n"
                combined_text += content + "\n"

                if len(matched_files) >= max_files:
                    break
        except Exception as e:
            pass

    tokens = count_tokens(combined_text)

    return {
        'approach': 'Traditional (grep + read full files)',
        'files_read': len(matched_files),
        'text': combined_text,
        'tokens': tokens
    }

