function progressFromLog(lines) {
    const text = (lines||[]).join(' ');
    let pct = 5;
    if (/Prepared \d+ chunks/i.test(text)) pct = 20;
    if (/BM25 index saved/i.test(text)) pct = 60;
    if (/Indexed \d+ chunks to Qdrant/i.test(text)) pct = 100;
    return pct;
}

