@app.get("/answer", response_model=Answer)

answer(
    q: str = Query(..., description="Question"),
    repo: Optional[str] = Query(None, description="Repository override: project|project")
):
    """Answer a question using strict per-repo routing.

    If `repo` is provided, retrieval and the answer header will use that repo.
    Otherwise, a lightweight router selects the repo from the query content.
    """
    g = get_graph()
    state = {"question": q, "documents": [], "generation":"", "iteration":0, "confidence":0.0, "repo": (repo.strip() if repo else None)}
    res = g.invoke(state, CFG)
    return {"answer": res["generation"]}

@app.get("/search")

load_repos() -> Dict[str, Any]:
    """
    Load repository configuration.

    Format:
      {
        "default_repo": "example-1",
        "repos": [
          {"name": "example-1", "path": "/abs/path/example-1",
           "keywords": ["auth", "backend"],
           "path_boosts": ["src/server/", "api/"],
           "layer_bonuses": {"server": {"kernel": 0.10}, "ui": {"ui": 0.12}}
          },
          {"name": "example-2", "path": ["/abs/path/example-2", "/abs/alt"]}
        ]
      }

    Fallbacks (if repos.json missing):
      - If REPO and REPO_PATH env set, synthesize a single-repo config.
      - Else raise a clear error when a function requires config.
    """
    global _CACHE
    if "config" in _CACHE:
        return _CACHE["config"]

    p = _repos_file_path()
    if p.exists():
        try:
            data = json.loads(p.read_text())
            if isinstance(data, dict) and isinstance(data.get("repos"), list):
                _CACHE["config"] = data
                return data
        except Exception:
            pass

    # Fallback to environment-only single repo
    env_repo = (os.getenv("REPO") or "default").strip()
    env_path = os.getenv("REPO_PATH") or os.getenv(f"REPO_{env_repo.upper()}_PATH")
    if env_path:
        cfg = {
            "default_repo": env_repo,
            "repos": [
                {"name": env_repo, "path": env_path}
            ]
        }
        _CACHE["config"] = cfg
        return cfg

    # Last resort minimal placeholder (no repos) to allow help text rendering
    cfg = {"default_repo": None, "repos": []}
    _CACHE["config"] = cfg
    return cfg

list_repos() -> List[str]:
    cfg = load_repos()
    return [str(r.get("name")) for r in cfg.get("repos", []) if r.get("name")]

get_default_repo() -> str:
    cfg = load_repos()
    # 1) explicit default
    if cfg.get("default_repo"):
        return str(cfg["default_repo"]).strip()
    # 2) first repo
    repos = cfg.get("repos", [])
    if repos:
        return str(repos[0].get("name"))
    # 3) env REPO or sentinel
    return (os.getenv("REPO") or "default").strip()

_find_repo(name: str) -> Optional[Dict[str, Any]]:
    name_low = (name or "").strip().lower()
    if not name_low:
        return None
    for r in load_repos().get("repos", []):
        if (r.get("name") or "").strip().lower() == name_low:
            return r
    return None

get_repo_paths(name: str) -> List[str]:
    r = _find_repo(name)
    if not r:
        raise ValueError(f"Unknown repo: {name}. Known: {', '.join(list_repos()) or '[]'}")
    p = r.get("path")
    if isinstance(p, list):
        return [str(Path(x).expanduser()) for x in p]
    if isinstance(p, str):
        return [str(Path(p).expanduser())]
    raise ValueError(f"Repo `{name}` missing 'path' in repos.json")

layer_bonuses(name: str) -> Dict[str, Dict[str, float]]:
    r = _find_repo(name)
    if not r:
        return {}
    lb = r.get("layer_bonuses") or {}
    # Normalize numeric values
    out: Dict[str, Dict[str, float]] = {}
    for intent, d in (lb.items() if isinstance(lb, dict) else []):
        if not isinstance(d, dict):
            continue
        out[intent] = {k: float(v) for k, v in d.items() if isinstance(v, (int, float))}
    return out

choose_repo_from_query(query: str, default: Optional[str] = None) -> str:
    q = (query or "").lower().strip()
    # Allow explicit prefix: "<name>: question"
    if ":" in q:
        cand, _ = q.split(":", 1)
        cand = cand.strip()
        if cand in [r.lower() for r in list_repos()]:
            return cand
    # Keyword voting across repos
    best = None
    best_hits = 0
    for name in list_repos():
        hits = 0
        for kw in get_repo_keywords(name):
            if kw and kw in q:
                hits += 1
        if hits > best_hits:
            best = name
            best_hits = hits
    if best:
        return best
    return (default or get_default_repo())

_classify_query(q:str)->str:
    ql=(q or '').lower()
    if any(k in ql for k in ['ui','react','component','tsx','page','frontend','render','css']):
        return 'ui'
    if any(k in ql for k in ['notification','pushover','apprise','hubspot','provider','integration','adapter','webhook']):
        return 'integration'
    if any(k in ql for k in ['diagnostic','health','event log','phi','mask','hipaa','middleware','auth','token','oauth','hmac']):
        return 'server'
    if any(k in ql for k in ['sdk','client library','python sdk','node sdk']):
        return 'sdk'
    if any(k in ql for k in ['infra','asterisk','sip','t.38','ami','freeswitch','egress','cloudflared']):
        return 'infra'
    return 'server'

