expand_queries(query: str, m: int = 4) -> list[str]:
    try:
        sys = "Rewrite a developer query into multiple search-friendly variants without changing meaning."
        user = f"Count: {m}\nQuery: {query}\nOutput one variant per line, no numbering."
        text, _ = generate_text(user_input=user, system_instructions=sys, reasoning_effort=None)
        lines = [ln.strip('- ').strip() for ln in (text or '').splitlines() if ln.strip()]
        uniq = []
        for ln in lines:
            if ln and ln not in uniq:
                uniq.append(ln)
        return (uniq or [query])[:m]
    except Exception:
        return [query]

generate_text(
    user_input: str,
    *,
    system_instructions: Optional[str] = None,
    model: Optional[str] = None,
    reasoning_effort: Optional[str] = None,  # e.g., "low" | "medium" | "high"
    response_format: Optional[Dict[str, Any]] = None,  # e.g., {"type":"json_object"}
    store: bool = False,
    previous_response_id: Optional[str] = None,
    extra: Optional[Dict[str, Any]] = None,
) -> Tuple[str, Any]:
    """
    Minimal wrapper over Responses API:
      - Uses 'instructions' for system prompt and 'input' for user text
      - Supports response_format (JSON mode / structured)
      - Leaves tool-calling to upstream if needed (MCP/file_search handled elsewhere)
    """
    mdl = model or _DEFAULT_MODEL
    kwargs: Dict[str, Any] = {
        "model": mdl,
        "input": user_input,
        "store": store,
    }
    if system_instructions:
        kwargs["instructions"] = system_instructions
    if reasoning_effort:
        kwargs["reasoning"] = {"effort": reasoning_effort}
    if response_format:
        kwargs["response_format"] = response_format
    if previous_response_id:
        kwargs["previous_response_id"] = previous_response_id
    if extra:
        kwargs.update(extra)

    # Check for MLX backend (Apple Silicon, highest priority for local models)
    ENRICH_BACKEND = os.getenv("ENRICH_BACKEND", "").lower()
    is_mlx_model = mdl.startswith("mlx-community/") if mdl else False
    prefer_mlx = (ENRICH_BACKEND == "mlx") or is_mlx_model

    if prefer_mlx:
        try:
            from mlx_lm import generate
            model, tokenizer = _get_mlx_model()

            # Build prompt with system instructions if provided
            sys_text = (system_instructions or "").strip()
            prompt = (f"<system>{sys_text}</system>\n" if sys_text else "") + user_input

            # Generate with MLX
            text = generate(
                model,
                tokenizer,
                prompt=prompt,
                max_tokens=2048,  # More tokens for answer generation
                verbose=False
            )
            return text, {"response": text, "backend": "mlx"}
        except Exception as e:
            # Fall through to Ollama/OpenAI on MLX failure
            pass

    # If using local Qwen via Ollama, prefer its API first
    OLLAMA_URL = os.getenv("OLLAMA_URL")
    prefer_ollama = bool(OLLAMA_URL) and ("qwen" in (mdl or "").lower())
    if prefer_ollama:
        try:
            import requests, json as _json, time
            sys_text = (system_instructions or "").strip()
            prompt = (f"<system>{sys_text}</system>\n" if sys_text else "") + user_input
            url = OLLAMA_URL.rstrip("/") + "/generate"

            # Retry configuration (production hardening)
            max_retries = 2
            chunk_timeout = 60  # 60s per chunk
            total_timeout = 300  # 5min hard cap

            for attempt in range(max_retries + 1):
                start_time = time.time()
                try:
                    # Prefer streaming to avoid long blocking on large models
                    with requests.post(url, json={
                        "model": mdl,
                        "prompt": prompt,
                        "stream": True,
                        "options": {"temperature": 0.2, "num_ctx": 8192},
                    }, timeout=chunk_timeout, stream=True) as r:
                        r.raise_for_status()
                        buf = []
                        last = None
                        for line in r.iter_lines(decode_unicode=True):
                            # Check total timeout
                            if time.time() - start_time > total_timeout:
                                # Return partial output on timeout
                                partial = ("".join(buf) or "").strip()
                                if partial:
                                    return partial + " [TIMEOUT]", {"response": partial, "timeout": True}
                                break

                            if not line:
                                continue
                            try:
                                obj = _json.loads(line)
                            except Exception:
                                continue
                            if isinstance(obj, dict):
                                seg = (obj.get("response") or "")
                                if seg:
                                    buf.append(seg)
                                last = obj
                                if obj.get("done") is True:
                                    break

                        text = ("".join(buf) or "").strip()
                        if text:
                            return text, (last or {"response": text})

                    # Fallback to non-stream if streaming returned empty
                    resp = requests.post(url, json={
                        "model": mdl,
                        "prompt": prompt,
                        "stream": False,
                        "options": {"temperature": 0.2, "num_ctx": 8192},
                    }, timeout=total_timeout)
                    resp.raise_for_status()
                    data = resp.json()
                    text = (data.get("response") or "").strip()
                    if text:
                        return text, data

                except (requests.Timeout, requests.ConnectionError) as e:
                    # Retry on network errors
                    if attempt < max_retries:
                        backoff = 2 ** attempt  # Exponential backoff: 1s, 2s
                        time.sleep(backoff)
                        continue
                    # Last attempt failed, fall through to OpenAI
                    pass
                except Exception:
                    # Other errors, don't retry
                    break
        except Exception:
            pass
    # Try OpenAI Responses API
    try:
        resp = client().responses.create(**kwargs)
        text = _extract_text(resp)
        return text, resp
    except Exception:
        # Fallback to Chat Completions for OpenAI-compatible servers that don't expose Responses API
        try:
            messages = []
            if system_instructions:
                messages.append({"role": "system", "content": system_instructions})
            messages.append({"role": "user", "content": user_input})
            ckwargs: Dict[str, Any] = {"model": mdl, "messages": messages}
            if response_format and isinstance(response_format, dict):
                ckwargs["response_format"] = response_format
            cc = client().chat.completions.create(**ckwargs)
            text = (cc.choices[0].message.content if getattr(cc, "choices", []) else "") or ""
            return text, cc
        except Exception as e:
            raise RuntimeError(f"Generation failed for model={mdl}: {e}")

watch_mode():
    """Watch for file changes and re-run eval."""
    print("â± Watch mode: monitoring for changes...")
    print(f"   Watching: {GOLDEN_PATH}, hybrid_search.py, langgraph_app.py")

    files_to_watch = [
        GOLDEN_PATH,
        "hybrid_search.py",
        "langgraph_app.py",
        "index_repo.py",
        "rerank.py"
    ]

    last_mtimes = {}
    for fp in files_to_watch:
        if os.path.exists(fp):
            last_mtimes[fp] = os.path.getmtime(fp)

    while True:
        time.sleep(5)
        changed = False
        for fp in files_to_watch:
            if not os.path.exists(fp):
                continue
            mtime = os.path.getmtime(fp)
            if fp not in last_mtimes or mtime > last_mtimes[fp]:
                print(f"\nðŸ”„ Change detected: {fp}")
                last_mtimes[fp] = mtime
                changed = True

        if changed:
            print("\n" + "="*60)
            print("Running eval...")
            print("="*60)
            results = run_eval_with_results()
            if "error" in results:
                print(f"Error: {results['error']}")
            else:
                print(json.dumps({
                    "top1_accuracy": results["top1_accuracy"],
                    "topk_accuracy": results["topk_accuracy"],
                    "total": results["total"],
                    "duration_secs": results["duration_secs"]
                }, indent=2))

# Python auto-imports sitecustomize at startup if present in sys.path.
# We use it to block legacy Chat Completions usage at runtime.
try:
    import openai  # type: ignore

    def _blocked(*_args, **_kwargs):  # noqa: D401
        raise RuntimeError(
            "Legacy Chat Completions API is disabled. Use Responses API via env_model.generate_text().\n"
            "Docs: https://openai.com/index/new-tools-and-features-in-the-responses-api/"
        )

    # Block classic patterns if present on this installed version
    if hasattr(openai, "ChatCompletion"):
        try:
            openai.ChatCompletion.create = staticmethod(_blocked)  # type: ignore[attr-defined]
        except Exception:
            pass
    # Some older clients expose nested chat.completions
    if hasattr(openai, "chat"):
        chat = getattr(openai, "chat")
        if hasattr(chat, "completions"):
            try:
                chat.completions.create = _blocked  # type: ignore[attr-defined]
            except Exception:
                pass
except Exception:
    # If openai not installed yet, do nothing.
    pass

lang_from_path(path:str)->Optional[str]:
    _, ext = os.path.splitext(path)
    return LANG_MAP.get(ext.lower())
nonws_len(s:str)->int:
    return len(re.sub(r"\s+", "", s))
extract_imports(src:str, lang:str)->List[str]:
    try:
        if _ts_get_parser is None:
            raise RuntimeError("tree_sitter_languages not available")
        parser = _ts_get_parser(lang)
        tree = parser.parse(bytes(src, "utf-8"))
        imports = []
        def walk(n):
            if n.type in IMPORT_NODES.get(lang, set()):
                imports.append(src[n.start_byte:n.end_byte])
            for c in n.children:
                walk(c)
        walk(tree.root_node)
        return imports
    except Exception:
        if lang == "python":
            return re.findall(r"^(?:from\s+[^\n]+|import\s+[^\n]+)$", src, flags=re.M)
        if lang in {"javascript","typescript"}:
            return re.findall(r"^import\s+[^\n]+;$", src, flags=re.M)
        return []

greedy_fallback(src:str, fpath:str, lang:str, target:int)->List[Dict]:
    sep = r"(?:\nclass\s+|\ndef\s+)" if lang=="python" else r"(?:\nclass\s+|\nfunction\s+)"
    parts = re.split(sep, src)
    if len(parts) < 2:
        out, cur, acc = [], [], 0
        for line in src.splitlines(True):
            cur.append(line); acc += nonws_len(line)
            if acc >= target:
                out.append("".join(cur)); cur, acc = [], 0
        if cur: out.append("".join(cur))
        return [{
            "id": hashlib.md5((fpath+str(i)+s[:80]).encode()).hexdigest()[:12],
            "file_path": fpath, "language": lang, "type":"blob","name":None,
            "start_line": 1, "end_line": s.count("\n")+1, "imports": extract_imports(src, lang), "code": s
        } for i,s in enumerate(out)]
    else:
        rejoined, buf, acc = [], [], 0
        for p in parts:
            if acc + nonws_len(p) > target and buf:
                s = "".join(buf); rejoined.append(s); buf, acc = [], 0
            buf.append(p); acc += nonws_len(p)
        if buf: rejoined.append("".join(buf))
        return [{
            "id": hashlib.md5((fpath+str(i)+s[:80]).encode()).hexdigest()[:12],
            "file_path": fpath, "language": lang, "type":"section","name":None,
            "start_line": 1, "end_line": s.count("\n")+1, "imports": extract_imports(s, lang), "code": s
        } for i,s in enumerate(rejoined)]

collect_files(roots:List[str])->List[str]:
    import fnmatch
    out=[]
    # Hardcoded skip dirs for safety
    skip_dirs = {".git","node_modules",".venv","venv","dist","build","__pycache__",".next",".turbo",".parcel-cache",".pytest_cache","vendor","third_party",".bundle","Pods"}

    # Try to load exclusion patterns from file
    exclude_patterns = []
    for root in roots:
        # Check for exclude_globs.txt in data/ directory
        parent_dir = os.path.dirname(root) if os.path.isfile(root) else root
        exclude_file = os.path.join(parent_dir, 'data', 'exclude_globs.txt')
        if os.path.exists(exclude_file):
            try:
                with open(exclude_file, 'r') as f:
                    patterns = [line.strip() for line in f if line.strip() and not line.startswith('#')]
                    exclude_patterns.extend(patterns)
            except Exception:
                pass

    for root in roots:
        for dp, dns, fns in os.walk(root):
            # Skip directories that match our exclusion rules
            dns[:] = [d for d in dns if d not in skip_dirs and not d.startswith('.venv') and not d.startswith('venv')]

            for fn in fns:
                p = os.path.join(dp, fn)

                # Check if file matches any exclusion pattern
                skip = False
                for pattern in exclude_patterns:
                    if fnmatch.fnmatch(p, pattern) or fnmatch.fnmatch(os.path.relpath(p, root), pattern):
                        skip = True
                        break

                if not skip and lang_from_path(p):
                    out.append(p)
    return out

enrich(file_path: str, lang: str, code: str) -> dict:
    prompt = PROMPT_TMPL.format(file=file_path, lang=lang, code=(code or '')[:4000])

    if ENRICH_BACKEND == "mlx":
        # MLX backend (Apple Silicon, fast)
        try:
            from mlx_lm import generate
            model, tokenizer = _get_mlx_model()
            txt = generate(model, tokenizer, prompt=prompt, max_tokens=150, verbose=False)
        except Exception as e:
            return {"summary": f"MLX error: {str(e)[:100]}", "keywords": []}
    else:
        # Ollama backend (fallback)
        import requests
        try:
            resp = requests.post(
                OLLAMA_URL,
                json={
                    "model": OLLAMA_MODEL,
                    "prompt": prompt,
                    "stream": False,
                    "options": {"temperature": 0.1, "num_ctx": 4096},
                },
                timeout=10,
            )
            resp.raise_for_status()
            txt = resp.json().get("response", "{}")
        except Exception as e:
            return {"summary": f"Ollama error: {str(e)[:100]}", "keywords": []}

    # Parse JSON response
    try:
        data = json.loads(txt)
        if isinstance(data, dict):
            return {"summary": data.get("summary", ""), "keywords": data.get("keywords", [])}
    except Exception:
        pass
    return {"summary": txt[:300], "keywords": []}

recreate_collection_safe(self, *args, **kwargs):
    try:
        return _orig_recreate(self, *args, **kwargs)
    except UnexpectedResponse as e:
        # Some servers return 404 on delete step inside recreate
        if getattr(e, "status_code", None) == 404:
            name, vectors_config = _extract_args(*args, **kwargs)
            return self.create_collection(collection_name=name, vectors_config=vectors_config)
        raise
    except Exception:
        # Very defensive fallback: try delete (ignore errors), then create
        name, vectors_config = _extract_args(*args, **kwargs)
        try:
            try:
                self.delete_collection(name)
            except Exception:
                pass
            return self.create_collection(collection_name=name, vectors_config=vectors_config)
        except Exception:
            raise


QdrantClient.recreate_collection = recreate_collection_safe

detect_layer(fp: str) -> str:
    f = (fp or '').lower()
    if REPO == 'project':
        if '/core/admin_ui/' in f or '/site/' in f or '/docs-site/' in f:
            return 'ui'
        if '/plugins/' in f or '/core/plugins/' in f or 'notification' in f or 'pushover' in f or 'apprise' in f:
            return 'plugin'
        if '/core/api/' in f or '/core/' in f or '/server' in f:
            return 'kernel'
        if '/docs/' in f or '/internal_docs/' in f:
            return 'docs'
        if '/tests/' in f or '/test_' in f:
            return 'tests'
        if '/infra/' in f or '/deploy/' in f or '/scripts/' in f:
            return 'infra'
        return 'kernel'
    else:
        if '/admin_ui/' in f or '/site/' in f or '/docs-site/' in f:
            return 'ui'
        if 'provider' in f or 'providers' in f or 'integration' in f or 'webhook' in f or 'adapter' in f:
            return 'integration'
        if '/api/' in f or '/backends/' in f or '/server' in f:
            return 'server'
        if '/sdks/' in f or '/python_mcp/' in f or '/node_mcp/' in f or '/plugin-dev-kit/' in f:
            return 'sdk'
        if '/docs/' in f or '/internal_docs/' in f:
            return 'docs'
        if '/asterisk/' in f or '/config/' in f or '/infra/' in f:
            return 'infra'
        return 'server'

VENDOR_MARKERS = (
    "/vendor/","/third_party/","/external/","/deps/","/node_modules/",
    "/Pods/","/Godeps/","/.bundle/","/bundle/"
)

detect_origin(fp: str) -> str:
    low = (fp or '').lower()
    for m in VENDOR_MARKERS:
        if m in low:
            return 'vendor'
    try:
        with open(fp, 'r', encoding='utf-8', errors='ignore') as f:
            head = ''.join([next(f) for _ in range(12)])
        if any(k in head.lower() for k in (
            'apache license','mit license','bsd license','mozilla public license'
        )):
            return 'vendor'
    except Exception:
        pass
    return 'first_party'
os.makedirs(OUTDIR, exist_ok=True)
_clip_for_openai(text: str, enc, max_tokens: int = 8000) -> str:
    toks = enc.encode(text)
    if len(toks) <= max_tokens:
        return text
    return enc.decode(toks[:max_tokens])
embed_texts(client: OpenAI, texts: List[str], batch: int = 64) -> List[List[float]]:
    # Legacy non-cached embedder (kept for compatibility if needed)
    embs = []
    enc = tiktoken.get_encoding('cl100k_base')
    for i in range(0, len(texts), batch):
        sub = [_clip_for_openai(t, enc) for t in texts[i:i+batch]]
        r = client.embeddings.create(model='text-embedding-3-large', input=sub)
        for d in r.data:
            embs.append(d.embedding)
    return embs

main() -> None:
    files = collect_files(BASES)
    print(f'Discovered {len(files)} source files.')
    all_chunks: List[Dict] = []
    for fp in files:
        if not should_index_file(fp):
            continue
        lang = lang_from_path(fp)
        if not lang:
            continue
        try:
            with open(fp, 'r', encoding='utf-8', errors='ignore') as f:
                src = f.read()
        except Exception:
            continue
        ch = chunk_code(src, fp, lang, target=900)
        all_chunks.extend(ch)

    seen, chunks = set(), []
    for c in all_chunks:
        c['repo'] = REPO
        try:
            c['layer'] = detect_layer(c.get('file_path',''))
        except Exception:
            c['layer'] = 'server'
        try:
            c['origin'] = detect_origin(c.get('file_path',''))
        except Exception:
            c['origin'] = 'first_party'
        h = hashlib.md5(c['code'].encode()).hexdigest()
        if h in seen:
            continue
        seen.add(h)
        c['hash'] = h
        chunks.append(c)
    print(f'Prepared {len(chunks)} chunks.')

    # Optional enrichment using a local code LLM via Ollama
    ENRICH = (os.getenv('ENRICH_CODE_CHUNKS', 'false') or 'false').lower() == 'true'
    if ENRICH:
        try:
            from metadata_enricher import enrich  # type: ignore
        except Exception:
            enrich = None
        if enrich is not None:
            for c in chunks:
                try:
                    meta = enrich(c.get('file_path',''), c.get('language',''), c.get('code',''))
                    c['summary'] = meta.get('summary','')
                    c['keywords'] = meta.get('keywords', [])
                except Exception:
                    c['summary'] = ''
                    c['keywords'] = []

    # BM25S index
    corpus: List[str] = []
    for c in chunks:
        pre = []
        if c.get('name'):
            pre += [c['name']]*2
        if c.get('imports'):
            pre += [i[0] or i[1] for i in c['imports'] if isinstance(i, (list, tuple))]
        body = c['code']
        corpus.append((' '.join(pre)+'\n'+body).strip())

    stemmer = Stemmer('english')
    tokenizer = Tokenizer(stemmer=stemmer, stopwords='en')
    corpus_tokens = tokenizer.tokenize(corpus)
    retriever = bm25s.BM25(method='lucene', k1=1.2, b=0.65)
    retriever.index(corpus_tokens)
    os.makedirs(os.path.join(OUTDIR, 'bm25_index'), exist_ok=True)
    # Workaround: ensure JSON-serializable vocab keys
    try:
        retriever.vocab_dict = {str(k): v for k, v in retriever.vocab_dict.items()}
    except Exception:
        pass
    retriever.save(os.path.join(OUTDIR, 'bm25_index'), corpus=corpus)
    tokenizer.save_vocab(save_dir=os.path.join(OUTDIR, 'bm25_index'))
    tokenizer.save_stopwords(save_dir=os.path.join(OUTDIR, 'bm25_index'))
    with open(os.path.join(OUTDIR, 'bm25_index', 'corpus.txt'), 'w', encoding='utf-8') as f:
        for doc in corpus:
            f.write(doc.replace('\n','\\n')+'\n')
    # Persist a stable mapping from BM25 doc index -> chunk id
    # Persist mapping from BM25 doc index -> chunk id (string)
    chunk_ids = [str(c['id']) for c in chunks]
    with open(os.path.join(OUTDIR, 'bm25_index', 'chunk_ids.txt'), 'w', encoding='utf-8') as f:
        for cid in chunk_ids:
            f.write(cid+'\n')
    # Also write a JSON map for convenience
    import json as _json
    _json.dump({str(i): cid for i, cid in enumerate(chunk_ids)}, open(os.path.join(OUTDIR,'bm25_index','bm25_map.json'),'w'))
    with open(os.path.join(OUTDIR,'chunks.jsonl'),'w',encoding='utf-8') as f:
        for c in chunks:
            f.write(json.dumps(c, ensure_ascii=False)+'\n')
    print('BM25 index saved.')

    # Optionally skip dense embeddings/Qdrant for fast local-only BM25 indexing
    if (os.getenv('SKIP_DENSE','0') or '0').strip() == '1':
        print('Skipping dense embeddings and Qdrant upsert (SKIP_DENSE=1).')
        return

    client = OpenAI(api_key=OPENAI_API_KEY) if OPENAI_API_KEY else None
    # Choose text for embedding (optionally enriched)
    texts = []
    for c in chunks:
        if c.get('summary') or c.get('keywords'):
            kw = ' '.join(c.get('keywords', []))
            texts.append(f"{c.get('file_path','') }\n{c.get('summary','')}\n{kw}\n{c.get('code','')}")
        else:
            texts.append(c['code'])
    embs: List[List[float]] = []
    et = (os.getenv('EMBEDDING_TYPE','openai') or 'openai').lower()
    if et == 'voyage':
        try:
            embs = embed_texts_voyage(texts, batch=64, output_dimension=int(os.getenv('VOYAGE_EMBED_DIM','512')))
        except Exception as e:
            print(f"Voyage embedding failed ({e}); falling back to local embeddings.")
            embs = []
        if not embs:
            embs = embed_texts_local(texts)
    elif et == 'mxbai':
        try:
            dim = int(os.getenv('EMBEDDING_DIM', '512'))
            embs = embed_texts_mxbai(texts, dim=dim)
        except Exception as e:
            print(f"MXBAI embedding failed ({e}); falling back to local embeddings.")
            embs = embed_texts_local(texts)
    elif et == 'local':
        embs = embed_texts_local(texts)
    else:
        if client is not None:
            try:
                cache = EmbeddingCache(OUTDIR)
                hashes = [c['hash'] for c in chunks]
                embs = cache.embed_texts(client, texts, hashes, model='text-embedding-3-large', batch=64)
                cache.save()
            except Exception as e:
                print(f'Embedding via OpenAI failed ({e}); falling back to local embeddings.')
        if not embs:
            embs = embed_texts_local(texts)
    point_ids: List[str] = []
    try:
        q = QdrantClient(url=QDRANT_URL)
        q.recreate_collection(
            collection_name=COLLECTION,
            vectors_config={'dense': models.VectorParams(size=len(embs[0]), distance=models.Distance.COSINE)}
        )
        points = []
        for c, v in zip(chunks, embs):
            # Derive a stable UUID from the chunk id string to satisfy Qdrant (expects int or UUID)
            cid = str(c['id'])
            pid = str(uuid.uuid5(uuid.NAMESPACE_DNS, cid))
            # Create slim payload without code (code is stored locally in chunks.jsonl)
            slim_payload = {
                'id': c.get('id'),
                'file_path': c.get('file_path'),
                'start_line': c.get('start_line'),
                'end_line': c.get('end_line'),
                'layer': c.get('layer'),
                'repo': c.get('repo'),
                'origin': c.get('origin'),
                'hash': c.get('hash'),
                'language': c.get('language')
            }
            # Remove None values to keep payload minimal
            slim_payload = {k: v for k, v in slim_payload.items() if v is not None}
            points.append(models.PointStruct(id=pid, vector={'dense': v}, payload=slim_payload))
            point_ids.append(pid)
            if len(points) == 64:
                q.upsert(COLLECTION, points=points)
                points = []
        if points:
            q.upsert(COLLECTION, points=points)
        # Persist point id mapping aligned to BM25 corpus order
        import json as _json
        _json.dump({str(i): pid for i, pid in enumerate(point_ids)}, open(os.path.join(OUTDIR,'bm25_index','bm25_point_ids.json'),'w'))
        print(f'Indexed {len(chunks)} chunks to Qdrant (embeddings: {len(embs[0])} dims).')
    except Exception as e:
        # Allow offline usage (BM25-only search) when Qdrant is unavailable
        print(f"Qdrant unavailable or failed to index ({e}); continuing with BM25-only index. Dense retrieval will be disabled.")

if __name__ == '__main__':
    main()

EmbeddingCache:
    def __init__(self, outdir: str):
        os.makedirs(outdir, exist_ok=True)
        self.path = os.path.join(outdir, "embed_cache.jsonl")
        self.cache = {}
        if os.path.exists(self.path):
            with open(self.path, "r", encoding="utf-8") as f:
                for line in f:
                    try:
                        o = json.loads(line)
                        self.cache[o["hash"]] = o["vec"]
                    except Exception:
                        pass

    def get(self, h: str):
        return self.cache.get(h)

    def put(self, h: str, v):
        self.cache[h] = v

    def save(self):
        with open(self.path, "w", encoding="utf-8") as f:
            for h, v in self.cache.items():
                f.write(json.dumps({"hash": h, "vec": v}) + "\n")

    def embed_texts(self, client, texts, hashes, model="text-embedding-3-large", batch=64):
        embs = [None] * len(texts)
        to_embed, idx_map = [], []
        for i, (t, h) in enumerate(zip(texts, hashes)):
            v = self.get(h)
            if v is None:
                idx_map.append(i)
                to_embed.append(t)
            else:
                embs[i] = v
        enc = tiktoken.get_encoding('cl100k_base')
        def _clip_for_openai(text: str, max_tokens: int = 8000) -> str:
            toks = enc.encode(text)
            if len(toks) <= max_tokens:
                return text
            return enc.decode(toks[:max_tokens])
        for i in range(0, len(to_embed), batch):
            sub = [_clip_for_openai(t) for t in to_embed[i:i+batch]]
            r = client.embeddings.create(model=model, input=sub)
            for j, d in enumerate(r.data):
                orig = idx_map[i + j]
                vec = d.embedding
                embs[orig] = vec
                self.put(hashes[orig], vec)
        return embs

MCPServer:
    """Minimal MCP server over stdio."""

    def __init__(self):
        self.graph = None
        self._init_graph()

    def _init_graph(self):
        """Lazy-load the LangGraph."""
        try:
            self.graph = build_graph()
        except Exception as e:
            self._error(f"Failed to initialize graph: {e}")

    def _error(self, msg: str):
        """Write error to stderr (MCP uses stdout for protocol)."""
        print(f"ERROR: {msg}", file=sys.stderr)

    def _log(self, msg: str):
        """Write log to stderr."""
        print(f"LOG: {msg}", file=sys.stderr)

    def handle_rag_answer(self, repo: str, question: str) -> Dict[str, Any]:
        """
        Execute full LangGraph pipeline: retrieval â†’ generation â†’ answer.
        Returns: {answer: str, citations: List[str], repo: str}
        """
        if not self.graph:
            self._init_graph()

        if not self.graph:
            return {
                "error": "Graph not initialized",
                "answer": "",
                "citations": [],
                "repo": repo or "unknown"
            }

        try:
            cfg = {"configurable": {"thread_id": f"mcp-{repo or 'default'}"}}
            state = {
                "question": question,
                "documents": [],
                "generation": "",
                "iteration": 0,
                "confidence": 0.0,
                "repo": repo
            }

            result = self.graph.invoke(state, cfg)

            # Extract citations from documents
            docs = result.get("documents", [])[:5]
            citations = [
                f"{d['file_path']}:{d['start_line']}-{d['end_line']}"
                for d in docs
            ]

            return {
                "answer": result.get("generation", ""),
                "citations": citations,
                "repo": result.get("repo", repo or "unknown"),
                "confidence": float(result.get("confidence", 0.0))
            }
        except Exception as e:
            self._error(f"rag.answer error: {e}")
            return {
                "error": str(e),
                "answer": "",
                "citations": [],
                "repo": repo or "unknown"
            }

    def handle_rag_search(self, repo: str, question: str, top_k: int = 10) -> Dict[str, Any]:
        """
        Retrieval-only path for debugging.
        Returns: {results: List[Dict], repo: str, count: int}
        """
        try:
            docs = search_routed_multi(
                question,
                repo_override=repo,
                m=4,
                final_k=top_k
            )

            # Return slim results (no code bodies for MCP transport)
            results = [
                {
                    "file_path": d.get("file_path", ""),
                    "start_line": d.get("start_line", 0),
                    "end_line": d.get("end_line", 0),
                    "language": d.get("language", ""),
                    "rerank_score": float(d.get("rerank_score", 0.0)),
                    "repo": d.get("repo", repo or "unknown")
                }
                for d in docs
            ]

            return {
                "results": results,
                "repo": repo or (results[0]["repo"] if results else "unknown"),
                "count": len(results)
            }
        except Exception as e:
            self._error(f"rag.search error: {e}")
            return {
                "error": str(e),
                "results": [],
                "repo": repo or "unknown",
                "count": 0
            }

    # --- Netlify helpers ---
    def _netlify_api(self, path: str, method: str = "GET", data: dict | None = None) -> dict:
        api_key = os.getenv("NETLIFY_API_KEY")
        if not api_key:
            raise RuntimeError("NETLIFY_API_KEY not set in environment")
        url = f"https://api.netlify.com/api/v1{path}"
        req = urllib.request.Request(url, method=method)
        req.add_header("Authorization", f"Bearer {api_key}")
        req.add_header("Content-Type", "application/json")
        body = None
        if data is not None:
            body = _json.dumps(data).encode("utf-8")
        try:
            with urllib.request.urlopen(req, data=body, timeout=30) as resp:
                raw = resp.read().decode("utf-8")
                return _json.loads(raw) if raw else {}
        except urllib.error.HTTPError as he:
            err_body = he.read().decode("utf-8", errors="ignore")
            raise RuntimeError(f"Netlify HTTP {he.code}: {err_body}")

    def _netlify_find_site_by_domain(self, domain: str) -> dict | None:
        sites = self._netlify_api("/sites", method="GET")
        if isinstance(sites, list):
            domain_low = (domain or "").strip().lower()
            for s in sites:
                for key in ("custom_domain", "url", "ssl_url"):
                    val = (s.get(key) or "").lower()
                    if val and domain_low in val:
                        return s
        return None

    def handle_netlify_deploy(self, domain: str) -> Dict[str, Any]:
        targets: list[str]
        if domain == "both":
            targets = ["project.net", "project.dev"]
        else:
            targets = [domain]
        results = []
        for d in targets:
            site = self._netlify_find_site_by_domain(d)
            if not site:
                results.append({"domain": d, "status": "not_found"})
                continue
            site_id = site.get("id")
            if not site_id:
                results.append({"domain": d, "status": "no_site_id"})
                continue
            try:
                build = self._netlify_api(f"/sites/{site_id}/builds", method="POST", data={})
                results.append({
                    "domain": d,
                    "status": "triggered",
                    "site_id": site_id,
                    "build_id": build.get("id"),
                })
            except Exception as e:
                results.append({"domain": d, "status": "error", "error": str(e)})
        return {"results": results}

    # --- Web tools (allowlisted) ---
    _WEB_ALLOWED = {"openai.com", "platform.openai.com", "github.com", "openai.github.io"}

    def _is_allowed_url(self, url: str) -> bool:
        try:
            u = urllib.parse.urlparse(url)
            host = (u.netloc or "").lower()
            # allow subdomains of allowed hosts
            return any(host == h or host.endswith("." + h) for h in self._WEB_ALLOWED)
        except Exception:
            return False

    def handle_web_get(self, url: str, max_bytes: int = 20000) -> Dict[str, Any]:
        if not (url or "").startswith("http"):
            return {"error": "url must start with http(s)"}
        if not self._is_allowed_url(url):
            return {"error": "host not allowlisted"}
        req = urllib.request.Request(url, method="GET", headers={"User-Agent": "project-rag-mcp/1.0"})
        try:
            with urllib.request.urlopen(req, timeout=20) as resp:
                raw = resp.read(max_bytes + 1)
                clipped = raw[:max_bytes]
                return {
                    "url": url,
                    "status": resp.status,
                    "length": len(raw),
                    "clipped": len(raw) > len(clipped),
                    "content_preview": clipped.decode("utf-8", errors="ignore")
                }
        except urllib.error.HTTPError as he:
            body = he.read().decode("utf-8", errors="ignore")
            return {"url": url, "status": he.code, "error": body[:1000]}
        except Exception as e:
            return {"url": url, "error": str(e)}

    def handle_request(self, request: Dict[str, Any]) -> Dict[str, Any]:
        """
        Handle MCP tool call request.

        Request format:
        {
          "jsonrpc": "2.0",
          "id": <request_id>,
          "method": "tools/call",
          "params": {
            "name": "rag.answer" | "rag.search",
            "arguments": {
              "repo": "project" | "project",
              "question": "...",
              "top_k": 10  # optional, search only
            }
          }
        }
        """
        method = request.get("method")
        req_id = request.get("id")

        if method == "tools/list":
            # Return available tools
            return {
                "jsonrpc": "2.0",
                "id": req_id,
                "result": {
                    "tools": [
                        {
                            "name": "rag_answer",
                            "description": "Get RAG answer with citations for a question in a specific repo (project|project)",
                            "inputSchema": {
                                "type": "object",
                                "properties": {
                                    "repo": {
                                        "type": "string",
                                        "description": "Repository name: 'project' or 'project'",
                                        "enum": ["project", "project"]
                                    },
                                    "question": {
                                        "type": "string",
                                        "description": "Developer question to answer from codebase"
                                    }
                                },
                                "required": ["repo", "question"]
                            }
                        },
                        {
                            "name": "rag_search",
                            "description": "Retrieval-only search (debugging) - returns relevant code locations without generation",
                            "inputSchema": {
                                "type": "object",
                                "properties": {
                                    "repo": {
                                        "type": "string",
                                        "description": "Repository name: 'project' or 'project'",
                                        "enum": ["project", "project"]
                                    },
                                    "question": {
                                        "type": "string",
                                        "description": "Search query for code retrieval"
                                    },
                                    "top_k": {
                                        "type": "integer",
                                        "description": "Number of results to return (default: 10)",
                                        "default": 10
                                    }
                                },
                                "required": ["repo", "question"]
                            }
                        },
                        {
                            "name": "netlify_deploy",
                            "description": "Trigger a Netlify build for project.net, project.dev, or both (uses NETLIFY_API_KEY)",
                            "inputSchema": {
                                "type": "object",
                                "properties": {
                                    "domain": {
                                        "type": "string",
                                        "description": "Target domain",
                                        "enum": ["project.net", "project.dev", "both"],
                                        "default": "both"
                                    }
                                }
                            }
                        },
                        {
                            "name": "web_get",
                            "description": "HTTP GET (allowlisted hosts only: openai.com, platform.openai.com, github.com, openai.github.io)",
                            "inputSchema": {
                                "type": "object",
                                "properties": {
                                    "url": {"type": "string", "description": "Absolute URL to fetch"},
                                    "max_bytes": {"type": "integer", "description": "Max bytes to return", "default": 20000}
                                },
                                "required": ["url"]
                            }
                        }
                    ]
                }
            }

        elif method == "tools/call":
            params = request.get("params", {})
            tool_name = params.get("name")
            args = params.get("arguments", {})

            # Backward-compat: accept legacy dotted names
            if tool_name in ("rag.answer", "rag_answer"):
                result = self.handle_rag_answer(
                    repo=args.get("repo"),
                    question=args.get("question", "")
                )
                return {
                    "jsonrpc": "2.0",
                    "id": req_id,
                    "result": {"content": [{"type": "text", "text": json.dumps(result, indent=2)}]}
                }

            elif tool_name in ("rag.search", "rag_search"):
                result = self.handle_rag_search(
                    repo=args.get("repo"),
                    question=args.get("question", ""),
                    top_k=args.get("top_k", 10)
                )
                return {
                    "jsonrpc": "2.0",
                    "id": req_id,
                    "result": {"content": [{"type": "text", "text": json.dumps(result, indent=2)}]}
                }

            elif tool_name in ("netlify.deploy", "netlify_deploy"):
                domain = args.get("domain", "both")
                result = self.handle_netlify_deploy(domain)
                return {
                    "jsonrpc": "2.0",
                    "id": req_id,
                    "result": {"content": [{"type": "text", "text": json.dumps(result, indent=2)}]}
                }

            elif tool_name in ("web.get", "web_get"):
                url = args.get("url", "")
                max_bytes = args.get("max_bytes", 20000)
                result = self.handle_web_get(url, max_bytes=max_bytes)
                return {
                    "jsonrpc": "2.0",
                    "id": req_id,
                    "result": {"content": [{"type": "text", "text": json.dumps(result, indent=2)}]}
                }

            else:
                return {
                    "jsonrpc": "2.0",
                    "id": req_id,
                    "error": {
                        "code": -32601,
                        "message": f"Unknown tool: {tool_name}"
                    }
                }

        elif method == "initialize":
            return {
                "jsonrpc": "2.0",
                "id": req_id,
                "result": {
                    "protocolVersion": "2024-11-05",
                    "capabilities": {
                        "tools": {}
                    },
                    "serverInfo": {
                        "name": "project-rag-mcp",
                        "version": "1.0.0"
                    }
                }
            }

        else:
            return {
                "jsonrpc": "2.0",
                "id": req_id,
                "error": {
                    "code": -32601,
                    "message": f"Method not found: {method}"
                }
            }

    def run(self):
        """Main stdio loop."""
        self._log("MCP server starting (stdio mode)...")

        for line in sys.stdin:
            line = line.strip()
            if not line:
                continue

            try:
                request = json.loads(line)
                response = self.handle_request(request)
                print(json.dumps(response), flush=True)
            except json.JSONDecodeError as e:
                self._error(f"Invalid JSON: {e}")
                print(json.dumps({
                    "jsonrpc": "2.0",
                    "id": None,
                    "error": {
                        "code": -32700,
                        "message": "Parse error"
                    }
                }), flush=True)
            except Exception as e:
                self._error(f"Unexpected error: {e}")
                print(json.dumps({
                    "jsonrpc": "2.0",
                    "id": None,
                    "error": {
                        "code": -32603,
                        "message": f"Internal error: {e}"
                    }
                }), flush=True)


if __name__ == "__main__":
    server = MCPServer()
    server.run()

import express from 'express';
import fetch from 'node-fetch';

const app = express();
const PORT = process.env.PORT || 8014;
const RAG_API_URL = process.env.RAG_API_URL || 'http://127.0.0.1:8012';

app.get('/health', (req, res) => {
  res.json({ status: 'ok', proxy: true, target: RAG_API_URL });
});

// JSON answer proxy
app.get('/mcp/answer', async (req, res) => {
  try {
    const { q, repo, token } = req.query;
    const u = new URL('/answer', RAG_API_URL);
    if (q) u.searchParams.set('q', q);
    if (repo) u.searchParams.set('repo', repo);
    const headers = token ? { Authorization: `Bearer ${token}` } : {};
    const r = await fetch(u.toString(), { headers });
    const data = await r.json();
    res.json(data);
  } catch (e) {
    res.status(500).json({ error: String(e) });
  }
});

// JSON search proxy
app.get('/mcp/search', async (req, res) => {
  try {
    const { q, repo, top_k, token } = req.query;
    const u = new URL('/search', RAG_API_URL);
    if (q) u.searchParams.set('q', q);
    if (repo) u.searchParams.set('repo', repo);
    if (top_k) u.searchParams.set('top_k', String(top_k));

    const headers = token ? { Authorization: `Bearer ${token}` } : {};
    const r = await fetch(u.toString(), { headers });
    const data = await r.json();
    res.json(data);
  } catch (e) {
    res.status(500).json({ error: String(e) });
  }
});

// SSE proxy for streaming answer
app.get('/mcp/answer_stream', async (req, res) => {
  try {
    const { q, repo, token } = req.query;
    const u = new URL('/answer_stream', RAG_API_URL);
    if (q) u.searchParams.set('q', q);
    if (repo) u.searchParams.set('repo', repo);
    const headers = token ? { Authorization: `Bearer ${token}` } : {};

    const r = await fetch(u.toString(), { headers });
    res.setHeader('Content-Type', 'text/event-stream; charset=utf-8');
    res.setHeader('Cache-Control', 'no-cache');
    res.setHeader('X-Accel-Buffering', 'no');

    if (!r.ok || !r.body) {
      res.write(`data: [ERROR] upstream ${r.status}\n\n`);
      return res.end();
    }

    const reader = r.body.getReader();
    const decoder = new TextDecoder();
    while (true) {
      const { done, value } = await reader.read();
      if (done) break;
      res.write(decoder.decode(value));
      // flush

    }
    res.end();
  } catch (e) {
    res.setHeader('Content-Type', 'text/event-stream; charset=utf-8');
    res.write(`data: [ERROR] ${String(e)}\n\n`);
    res.end();
  }
});

app.listen(PORT, () => {
  console.log(`Node proxy listening on :${PORT}, targeting ${RAG_API_URL}`);
});

